import { scaleOrdinal } from 'd3-scale';
import { min, max, extent } from 'd3-array';
class Scales {
  constructor(xLabels,yLabels, dims){
    this.setScales(xLabels,yLabels, dims);
  };
  setScales=(xLabels,yLabels, dims)=>{
    this.xScale =
      scaleOrdinal()
        .domain([min(xLabels), max(xLabels)])
        .range([dims.innerWidth, 0])
    this.yScale =
      scaleOrdinal()
        .domain([min(yLabels), max(yLabels)])
        .range([0, dims.innerHeight])
  };
};
export default Scales;
