import  'd3-selection';
import 'd3-transition';
import * as d3 from 'd3'
import d3Tip from 'd3-tip';

const tip = (text)=> d3Tip().attr('class', 'd3-tip')
  .offset([-10, 0])
  .html(function(d) {
    return "<strong>Size Breakdown Quantity</strong> <span style='color:white'>" + text + "</span>";
  })


class Circles {
  constructor(chart, data, scales){
    this.chart = chart;
    this.scales = scales;
    this.updateData(data);
  };

  updateData=(newData)=>{
    this.data = newData;

    this.allCircles =
      this.chart.selectAll('.myCircle').data(this.data);
    this.allCircles.transition().duration(1000)
        .attr('cx', (d,i)=>this.scales.xScale(d["column"],d["rowKey"]))
        .attr('cy', (d)=>this.scales.yScale(d["rowKey"]))
        .attr('fill', (d) => d["color"])
        .on('mouseover', tip.show)
        .on('mouseout', tip.hide)
    this.enter();
  };
  enter=()=>{

    this.allCircles.enter()
        .append('circle')
        .attr('class', 'myCircle')
        .attr('cx', (d,i)=>this.scales.xScale(d["column"],d["rowKey"]))
        .attr('cy', (d)=>this.scales.yScale(d["rowKey"]))
        .attr('r', (d) => d["value"] * 3)
        .attr('fill', (d) => d["color"])
        .attr("opacity", 0.8)
        .call(tip)
        .on("mouseover", tip.show)
        .on('mouseout', tip.hide)


    this.exit();
  };
  exit=()=>{
    this.allCircles.exit().remove();
  };
  updateScales=(newScales)=>{
    this.scales = newScales;
    this.allCircles =
      this.chart.selectAll('.myCircle')
        .prototype.transition().duration(500)
          .attr('cx', (d,i)=>this.scales.xScale(d["column"],d["rowKey"]))
          .attr('cy', (d)=>this.scales.yScale(d["rowKey"]))
  };
};
export default Circles;
