import React from 'react';
import './App.css';
import ReactNode from './ReactNode';
import { Provider } from 'react-dims';


function App() {
  var data = [{
                label:"REGULAR",
                product_id: 1,
                //color:"#F44336",
                data:[
                      {y:"32", items:[{x:"A", value:0},{x:"B", value:1},{x:"D", value:2}]},
                      {y:"34", items:[{x:"A", value:3},{x:"B", value:4},{x:"D", value:5}]},
                      {y:"36", items:[{x:"A", value:6},{x:"B", value:7},{x:"D", value:8}]},
                    ] 
            },
            {
                label:"LARGE",
                product_id:2,
               // color:"#202120",
                data:[
                      {y:"34", items:[{x:"C", value:0},{x:"D", value:1},{x:"DD", value:2}]},
                      {y:"36", items:[{x:"D", value:3},{x:"DD", value:4},{x:"F", value:5}]},
                      {y:"40", items:[{x:"DD", value:6},{x:"F", value:7},{x:"FF", value:8}]},
                      {y:"42", items:[{x:"DD", value:6},{x:"F", value:7},{x:"FF", value:8}]},
                    ]
            },
]
  var options ={
    titleSettings: {title:"SIZE BREAKDOWN MATRIX", fontColor:"#fff"},
    legendSettings: { position: 'right', align:'top', visible: true, height: '100', width: '400', category: "label", 
                    title:"PRODUCT TYPE", fontColor:"#fff",
                    showIntersection:true, 
                    labelIntersection:"SIZE INTERSECTION", 
                    colorIntersection:"#fff459"},
    chartSettings: {fontColor:"#fff"}
  }

  return (
    <div className="myLayout">
      <div className="coolThing">
        <Provider className="provider">
          <ReactNode data={data} color="True" options={options}/>
        </Provider>
      </div>
    </div>
  );
}

export default App;
