import React from 'react';
import { Square } from './utilities';
import * as R from 'ramda';

class Matrix extends React.Component {

	constructor(props) {
		super(props);
	};
	init=(data, dims)=>{
	    this.data = data;
	    this.dims = new Dimensions(dims); 
	    this.chart = this.svg.append('g');
	    this.chart.attr('transform', `translate(${this.dims.margin.left}, ${this.dims.margin.top})`);
	    this.scales = new Scales(this.data, this.dims);//
	    this.axes = new Axes(this.chart, this.scales, this.dims);

	  };
	renderSquare(item, className="square") {
		return <Square value={item.value} className = {className}/>;
	};

	render() {
		return (
			<div className="container">
			{
				this.props.data.map(row => (
					<div className="board-row" key={row.uniqueId}>{ R.map(this.renderSquare, row) }</div>
				))
			}
			</div>
		)


	};
};
export default Matrix;
