import React from 'react';
import { Circles, Intersection } from './utilities';

class Legend extends React.Component{
	constructor(chart, options, dims, data, scales) {
		super(chart, options);
		this.chart = chart;
		this.options = options;
		this.dims = dims;
		this.data = data;
		this.scales = scales;
		this.init();
	};

	enter = () => {
		console.log("intra");
		this.chart.append('g')
			.attr('class', 'legend');


		this.chart.selectAll('g .legend')
			.append('rect')
			.attr('x', this.dims.innerWidth )
			.attr('y', this.dims.innerHeight - 100)
			.attr('fill', '#efefef')
			.attr('class', 'legend');

		this.chart.selectAll('legendCircle').data(this.data);

		console.log("legend data", this.data);

		this.chart.selectAll('rect .legend')
			.append('circle')
		    .attr('class', 'legendCircle')
		    .attr('cx', (d,i)=>this.scales.xScale(d["column"],d["rowKey"]))
		    .attr('cy', (d)=>this.scales.yScale(d["rowKey"]))
		    .attr('r', (d) => d["value"] * 3)
		    .attr('fill', (d) => d["color"])
		    .attr("opacity", 0.8);

        this.exit();
	};

}

export default Legend;
