import  'd3-selection';
import 'd3-transition';
import { tip} from '../functions';
import * as R from 'ramda';

class Intersection {
	constructor(chart, data, scales,dims){
	    this.chart = chart;
	    this.scales = scales;
	    this.dims = dims;
	    this.updateData(data);
	};

	enter=()=>{
		//to add ticks step or x values array len instead of the 8 in  this.dims.innerHeight/8
		var xbandSize = this.scales.xScale.bandwidth();
    	var ybandSize = this.scales.yScale.bandwidth();

		this.allIntersection.enter()
			.append('circle')
			.attr('class', 'intersection')
			.attr('cx', (d,i)=>{return this.scales.xScale(d.x,d.y)-this.dims.innerHeight/10})
        	.attr('cy', (d)=>this.scales.yScale(d.y) + 20)
        	.attr('r', 10)
        	.attr('fill', (d) => d.color)
        	.call(tip)
	        .on("mouseover", tip.show)
	        .on('mouseout', tip.hide);

        this.allIntersection.enter()
			.append('rect')
			.attr('class', 'intersectionText')
	      	.attr('x', (d,i)=>{return this.scales.xScale(d.x,d.y) })
        	.attr('y', (d)=>this.scales.yScale(d.y))
        	.style('width', xbandSize)
        	.style('heigth', ybandSize)

	};
	updateData=(newData)=>{
	    this.data = newData;

	    this.allIntersection =
	      this.chart.append('g')
                .attr('class', 'intersectionMarks')
	      		.selectAll('.intersection').data(this.data);

	    this.allIntersection
			.attr('x', (d,i)=>this.scales.xScale(d.x,d.y))
        	.attr('y', (d)=>this.scales.yScale(d.y))
	        .attr('fill', (d) => d.color)
	        .on('mouseover', tip.show)
        	.on('mouseout', tip.hide)
	    this.enter();
	};
	exit=()=>{
		this.allIntersection.exit().remove();
	};
	updateScales=(newScales)=>{

    this.scales = newScales;

    var xbandSize = this.scales.xScale.bandwidth();
    var ybandSize = this.scales.yScale.bandwidth();

    this.allIntersection =
      this.chart.selectAll('.intersection')
        .attr('cx', (d,i)=>{return this.scales.xScale(d.x,d.y)})
        .attr('cy', (d)=>this.scales.yScale(d.y) + ybandSize/4)

  };


}

export default Intersection;
