import React from 'react';
import { Square , XAxis} from './utilities';
import * as R from 'ramda';

class Matrix extends React.Component {
	constructor(props) {
		super(props);
	};
	init=(data,dims) =>{

	};
	renderSquare(item, className="square") {
		return <Square item={item} className = {className} />;
	};
	renderxAxis(item, className="xAxis") {
		return <XAxis item={item} className = {className} />;
	};
	xAxis(items, className="xAxis") {
		const xScaleKey = 'column';
		console.log("items",items)

		var flatitems = items.flat()
		console.log(flatitems.flat())

		const getXLabels = R.pipe(
			R.flatten,
			R.map(R.prop(xScaleKey)),
			R.uniq
			)

		const xLabels = getXLabels(items);
		console.log("xlabels")
		console.log(xLabels);
		return xLabels;
	};

	getDatasource(dataRaw) {

		return dataRaw.map(ds => (ds.data))
	}

	render() {
		var dsItems = this.getDatasource(this.props.data)
		//console.log(dsItems.map(row => (row.map(rowItems => (rowItems.items)))))
		return (
			<div className="container">
			{
				dsItems.map(rowItems => (rowItems.map(row => (
					<div className="board-row" key={row.uniqueId}>
						<div className="yAxis">
							{row.rowKey}
						</div>
						{R.map(this.renderSquare, row.items)}

					</div>
				))))
			}
				<div className="board-row-axis">
					{this.renderSquare([], "squareInvisible")}
					{ 
						R.map(this.renderxAxis, this.xAxis(dsItems.map(row => (row.map(rowItems => (rowItems.items))))))
					}
				</div>
			</div>
		)


	};
};
export default Matrix;
