import React from 'react';
import { axisBottom, axisLeft } from 'd3-axis';


class Axes {
  constructor(parent, scales, dims, options){
    this.parent = parent;
    this.createAxes(parent, scales, dims, options);
  };
  createAxes=(parent, scales, dims, options)=>{
    this.scaleAxes(scales, dims);
    this.xAxisBottomG =
      this.parent.append('g')
        .attr('transform', `translate(0, ${dims.innerHeight})`)
        .call(this.xAxisBottom)


    this.yAxisLeftG =
      this.parent.append('g')
      .call(this.yAxisLeft)

  };
  scaleAxes=(scales, dims)=>{
    this.xAxisBottom =
      axisBottom()
        .scale(scales.xScale)
        .tickSize(0)
        .tickFormat('')
        .tickPadding(20);


    this.xAxisBottomText =
      axisBottom()
        .scale(scales.xScale)
        .tickSize(0)


    this.yAxisLeft =
      axisLeft()
        .scale(scales.yScale)
        .tickSize(0)
        .tickPadding(20);
  };
  exit=()=>{
    this.xAxisBottomG.remove();
    this.yAxisLeftG.remove();

  };
  updateAxes=(scales, dims)=>{
    this.scaleAxes(scales, dims);
    var xbandSize = scales.xScale.bandwidth();


    this.xAxisBottomG = this.parent.append('g')
      .attr('transform', `translate(0, ${dims.innerHeight})`)
      .call(this.xAxisBottom)
      .call(this.xAxisBottomText)
      .selectAll("text")
         .style("text-anchor", "middle")
         .attr("transform", "translate(" + -xbandSize/2 + ", 0)");


    this.yAxisLeftG = this.parent.append('g')
      .call(this.yAxisLeft)
  };
};
export default Axes;
