import React from 'react';
import { Square } from './utilities';

class Matrix extends React.Component {

	constructor(data) {
		super();
		this.data = data;
	}


	renderSquare(i, className="square") {
		return <Square value={i} className = {className}/>;
	};

	render() {
		return (
			<div className="container">
				  <div className="board-row">
			          {this.renderSquare("", "squareInvisible")}
			          {this.renderSquare(1)}
			          {this.renderSquare(2)}
			        </div>
			        <div className="board-row">
			          {this.renderSquare(3)}
			          {this.renderSquare(4)}
			          {this.renderSquare(5)}
			        </div>
			        <div className="board-row">
			          {this.renderSquare(6)}
			          {this.renderSquare(7)}
			          {this.renderSquare(8)}
			        </div>
			</div>
			);
	};
};
export default Matrix;
