import React from 'react';
import { axisBottom, axisLeft } from 'd3-axis';


class Axes {
  constructor(parent, scales, dims, options){
    this.options = options;
    this.createAxes(parent, scales, dims, options);
  };
  createAxes=(parent, scales, dims, options)=>{

    this.scaleAxes(scales, dims);
    this.xAxisBottomG =
      parent.append('g')
        .attr('transform', `translate(0, ${-dims.innerHeigth})`)
        .call(this.xAxisBottom)
        .style('stroke-width', 0.5)
        .selectAll(".tick text")
        .attr("x", -dims.innerWidth/10)
        .attr('class', 'axis')
        .style('fill', options.fontColor);


    this.yAxisLeftG =
      parent.append('g')
        .call(this.yAxisLeft)
        .style('stroke-width', 0.5)
        .selectAll(".tick text")
        .attr("dy", -30)
        .attr('class', 'axis')
        .style('fill', options.fontColor);

  };
  scaleAxes=(scales, dims)=>{
    this.xAxisBottom =
      axisBottom()
        .scale(scales.xScale)
        .tickSize(-50)

    this.yAxisLeft =
      axisLeft()
        .scale(scales.yScale)
        .tickSize(-50)
  };
  updateAxes=(scales, dims)=>{
    console.log("updateAxes", scales,dims)
    this.scaleAxes(scales, dims);

    this.xAxisBottomG
      .attr('transform', `translate(0, ${dims.innerWidth})`)
      .call(this.xAxisBottom)
        .style('stroke-width', 0.5)
        .selectAll(".tick text")
        .attr("x", -dims.innerHeight/10)
        .attr('class', 'axis')
        .style('fill', this.options.fontColor);

    this.yAxisLeftG
      .attr('transform', `translate(0, ${dims.innerHeight})`)
      .call(this.yAxisLeft)
      .style('stroke-width', 0.5)
        .selectAll(".tick text")
        .attr("dy", -30)
        .attr('class', 'axis')
        .style('fill', this.options.fontColor);
  };
};
export default Axes;
