import { scalePoint, scaleBand } from 'd3-scale';

class Scales {
  constructor(xLabels,yLabels, dims){
    this.setScales(xLabels,yLabels, dims);
  };
  setScales=(xLabels,yLabels, dims)=>{
    console.log("xLabels,yLabels",xLabels,yLabels);
    this.xScale =
      scaleBand()
        .domain(xLabels)
        .range([0,dims.innerWidth])
        .padding(0.8)

    this.yScale =
      scaleBand()
        .domain(yLabels)
        .range([0,dims.innerHeight])
        .paddingOuter(0.8)

  };
};
export default Scales;
