import React from 'react';
import { Circles, Intersection, Dimensions } from './utilities';

class Legend extends React.Component{
	constructor(chart, options, dims, data, scales) {
		super(chart, options);
		this.chart = chart;
		this.options = options;
		this.dims = dims;
		this.scales = scales;
		this.dimLegend = new Dimensions({width:140, height:100});

		this.legend = this.chart.append('g').attr('transform', `translate(${this.dims.innerWidth}, ${this.dims.innerHeight})`)
			.attr('class', 'legend');
		this.legend.append('text')
	        .text(this.options.title)
	        .attr('x', 10)
	        .attr('y', -this.dims.innerHeight)
	        .attr('class', 'legendTitle');

		var legendIntersectionObj ={
			'label': this.options.labelIntersection,
			'color': this.options.colorIntersection
		}
		if (this.options.showIntersection)
			data.push(legendIntersectionObj);
		this.updateData(data);
	};

	updateData = (newData) => {
		this.data = newData;

		this.allLegend = this.legend.selectAll('.legendCircle').data(this.data);
		this.allLegend
	        .attr('cx', (d) => {return 10})
	        .attr('cy', (d,i) => {return this.dimLegend.innerHeight*(i+1)})
	        .attr('fill', (d) => d.color)
   		this.enter();
	};

	enter = () => {

		this.allLegend.enter()
	        .append('circle')
	        .attr('class', 'legendCircle')
	        .attr('cx', (d)=> {return 10})
	        .attr('cy', (d, i)=>{return this.dimLegend.innerHeight*(i+1) })
	        .attr('r', (d) => 7)
	        .attr('fill', (d) => {return d.color})
	        .attr("opacity", 0.8);

	    this.allLegend.enter().append('text')
	        .text((d) => {return d.label;})
	        .attr('x', 20)
	        .attr('y', (d, i)=>{return this.dimLegend.innerHeight*(i+1) + 3})
	        .attr('class', 'legendText');





        this.exit();
	};
	exit=()=>{
	    this.allLegend.exit().remove();
	};

}

export default Legend;
