import { axisBottom, axisLeft, axisPropsFromBandedScale } from 'd3-axis';

 const TickAxis = (axis) =>{
      const ticks = axis.ticks();
      return (
          ticks.map(tick => {<div className="square">1</div>})
        )
  };

class Axes {
  constructor(parent, scales, dims){
    this.createAxes(parent, scales, dims);
  };
  createAxes=(parent, scales, dims)=>{
    this.scaleAxes(scales, dims);
    this.xAxisBottomG =
      parent.append('g')
        .attr('transform', `translate(0, ${dims.innerHeight})`)
        .call(this.xAxisBottom)
        .style('stroke-width', 0)
        .selectAll(".tick text")
        .attr("x", -dims.innerHeight/10)
        .attr('class', 'axis')


    this.yAxisLeftG =
      parent.append('g')
        .call(this.yAxisLeft)
        .style('stroke-width', 0.05)
        .selectAll(".tick text")
        .attr("dy", -30)
        .attr('class', 'axis')

  };
  scaleAxes=(scales, dims)=>{
    this.xAxisBottom =
      axisBottom()
        .scale(scales.xScale)
        .tickSize(-dims.innerHeight)
        .tickPadding(10)
    TickAxis(this.xAxisBottom);

    this.yAxisLeft =
      axisLeft()
        .scale(scales.yScale)
        .tickSize(-dims.innerWidth)
        .tickPadding(10)
  };
  updateAxes=(scales, dims)=>{
    this.scaleAxes(scales, dims);
    this.xAxisBottomG
      .attr('transform', `translate(0, ${dims.innerHeight})`)
      .call(this.xAxisBottom)
    this.yAxisLeftG
      .call(this.yAxisLeft)
  };
};
export default Axes;
