import  'd3-selection';
import 'd3-transition';
import { tip} from '../functions';

class Intersection {
	constructor(chart, data, scales,dims){
	    this.chart = chart;
	    this.scales = scales;
	    this.dims = dims;
	    this.updateData(data);
	};

	enter=()=>{
		//to add ticks step or x values array len instead of the 8 in  this.dims.innerHeight/8
		this.allIntersection.enter()
			.append('circle')
			.attr('class', 'intersection')
			.attr('cx', (d,i)=>{return this.scales.xScale(d.x,d.y)-this.dims.innerHeight/10})
        	.attr('cy', (d)=>this.scales.yScale(d.y) + 20)
        	.attr('r', 10)
        	.attr('fill', (d) => d["color"])
        	.call(tip)
	        .on("mouseover", tip.show)
	        .on('mouseout', tip.hide)

        this.allIntersection.enter()
			.append('text')
			.attr('class', 'intersection')
	      	.text('!')
	      	.attr('x', (d,i)=>{return this.scales.xScale(d.x,d.y)-this.dims.innerHeight/10 -1})
        	.attr('y', (d)=>this.scales.yScale(d.y) + 24)

	};
	updateData=(newData)=>{
	    this.data = newData;

	    this.allIntersection =
	      this.chart.selectAll('.intersection').data(this.data);
	    this.allIntersection.transition().duration(1000)
			.attr('x', (d,i)=>this.scales.xScale(d.x,d.y))
        	.attr('y', (d)=>this.scales.yScale(d.y))
	        .attr('fill', (d) => d.color)
	    this.enter();
	};
	exit=()=>{
		this.allIntersection.exit().remove();
	};


}

export default Intersection;
