import { scalePoint, scaleBand, scaleLinear } from 'd3-scale';
import { min, max } from 'd3';

class Scales {
  constructor(xLabels,yLabels, dims, values){

    this.setScales(xLabels,yLabels, dims);
    this.setRadiusScale(values);
  };
  setScales=(xLabels,yLabels, dims)=>{
    console.log("xLabels in scales", xLabels)
    console.log("xLabels in scales", yLabels)
    this.xScale = scaleBand()
        .domain(xLabels)
        .range([0,dims.innerWidth-50])
        .paddingOuter(0.5)

    this.yScale = scaleBand()
        .domain(yLabels)
        .range([0,dims.innerHeight-50])
        .paddingOuter(0.5)

  };
  setRadiusScale=(values)=>{
    this.rScale = scaleLinear()
        .domain([1, max(values)])
        .range([3, 20])
        .nice();

  };
};
export default Scales;
