	useEffect(()=>{
	    createCanvas(()=>new ThePattern(domNode.current));
	  },[]);
	useEffect(()=>{
	    if(data.length>1 && dims.width && vizInitialized===false){
	      canvas.init(data, dims);
	      setVizInitialized(()=>true);
	    };
	},[data, dims]);
	return (
		<div ref={domNode} style={{display: 'grid', height: '100%'}}/>
