import React from 'react';
import { Circles, Intersection, Dimensions } from './utilities';

class Legend extends React.Component{
	constructor(chart, options, dims, data, scales) {
		super(chart, options);
		this.chart = chart;
		this.options = options;
		this.dims = dims;
		this.scales = scales;

		this.legend = this.chart.append('g').attr('transform', `translate(${this.dims.innerWidth}, ${this.dims.innerHeight})`)
			.attr('class', 'legend');

		this.legend
			.append('rect')
			.attr('x', 0)
			.attr('y', -90)
			.attr('fill', '#efefef')
			.attr('class', 'legend'); 

		console.log("legend data", data);

		this.rect = this.legend.selectAll('rect .legend');

		console.log("Legend rect",this.rect);

		this.dimLegend = new Dimensions({width:100, height:100});

		console.log("Dim Legend",this.dimLegend);

		this.updateData(data);
	};

	updateData = (newData) => {
		this.data = newData;

		console.log("legend", this.data);
		this.allLegend = this.legend.selectAll('.legendCircle').data(this.data);
		this.allLegend
	        .attr('cx', (d) => {return 10})
	        .attr('cy', (d,i) => {return this.dimLegend.innerHeight*i})
	        .attr('fill', (d) => d.color)
   		this.enter();
	};

	enter = () => {
		this.allLegend.enter()
	        .append('circle')
	        .attr('class', 'legendCircle')
	        .attr('cx', (d)=> {return 10})
	        .attr('cy', (d, i)=>{return this.dimLegend.innerHeight*i})
	        .attr('r', (d) => 5)
	        .attr('fill', (d) => d.color)
	        .attr("opacity", 0.8)
        this.exit();
	};
	exit=()=>{
	    this.allLegend.exit().remove();
	};

}

export default Legend;
