import React from 'react';
import { axisBottom, axisLeft } from 'd3-axis';


class Axes {
  constructor(parent, scales, dims){
    this.createAxes(parent, scales, dims);
  };
  createAxes=(parent, scales, dims)=>{
    this.scaleAxes(scales, dims);
    this.xAxisBottomG =
      parent.append('g')
        .attr('transform', `translate(10, ${dims.innerHeight})`)
        .call(this.xAxisBottom)
        .selectAll(".tick text")
        .attr("x", -dims.innerHeight/7 -10)
        .attr('class', 'axis')


    this.yAxisLeftG =
      parent.append('g')
        .call(this.yAxisLeft)
        .selectAll(".tick text")
        .attr("dy", -30)
        .attr('class', 'axis')

    // parent.selectAll("g .tick")
    //     .append("rect")
    //     .attr('width', 40)
    //     .attr('stroke-width', '1')
    //     .attr('stroke-color', 'green')
    //     .attr('height', 40)
       // .attr("opacity", 0.3)

  };
  scaleAxes=(scales, dims)=>{
    this.xAxisBottom =
      axisBottom()
        .scale(scales.xScale)
        //.tickSize(-dims.innerHeight)
       // .tickPadding(10)

    this.yAxisLeft =
      axisLeft()
        .scale(scales.yScale)
        //.tickSize(-dims.innerWidth)
       // .tickPadding(10)
  };
  updateAxes=(scales, dims)=>{
    this.scaleAxes(scales, dims);
    this.xAxisBottomG
      .attr('transform', `translate(0, ${dims.innerHeight})`)
      .call(this.xAxisBottom)
    this.yAxisLeftG
      .call(this.yAxisLeft)
  };
};
export default Axes;
