import  'd3-selection';
import 'd3-transition';
import * as d3 from 'd3';
import 'd3-tip';

d3.tip = tip;

 const tip = d3.tip()
      .attr("class", "d3-tip")
      .offset([-8, 0])
      .html(function(d) { return "Data: " + d; });

class Circles {
  constructor(chart, data, scales){
    this.chart = chart;
    this.scales = scales;
    this.updateData(data);
    this.chart.call(tool_tip);
  };

  updateData=(newData)=>{
    this.data = newData;

    this.allCircles =
      this.chart.selectAll('.myCircle').data(this.data);
    this.allCircles.transition().duration(1000)
        .attr('cx', (d,i)=>this.scales.xScale(d["column"],d["rowKey"]))
        .attr('cy', (d)=>this.scales.yScale(d["rowKey"]))
        .attr('fill', (d) => d["color"])
        .on('mouseover', tip.show)
        .on('mouseout', tip.hide)
    this.enter();
  };
  enter=()=>{
    this.allCircles.enter()
        .append('circle')
        .attr('class', 'myCircle')
        .attr('cx', (d,i)=>this.scales.xScale(d["column"],d["rowKey"]))
        .attr('cy', (d)=>this.scales.yScale(d["rowKey"]))
        .attr('r', (d) => d["value"] * 3)
        .attr('fill', (d) => d["color"])
        .attr("opacity", 0.8)
        .on("mouseover", function(d) {
            tip.show(d, this);
        })
        .on('mouseout', tip.hide)

    this.exit();
  };
  exit=()=>{
    this.allCircles.exit().remove();
  };
  updateScales=(newScales)=>{
    this.scales = newScales;
    this.allCircles =
      this.chart.selectAll('.myCircle')
        .prototype.transition().duration(500)
          .attr('cx', (d,i)=>this.scales.xScale(d["column"],d["rowKey"]))
          .attr('cy', (d)=>this.scales.yScale(d["rowKey"]))
  };
};
export default Circles;
