	};
	renderSquare(item, className="square") {
		return <Square item={item} className = {className} />;
	};


	render() {
		const xScaleKey = 'rowKey';
		const yScaleKey = 'column';
		let rows = R.groupBy(R.prop(xScaleKey), this.props.data);

		const matrix = R.mapObjIndexed(R.sortBy(R.prop(yScaleKey)), rows);

		const getXLabels = R.keys;

		const getYLabels = R.compose(
		  R.map(R.prop(yScaleKey)), 
		  R.head, 
		  R.values
		)
		console.log(getXLabels(matrix));
		return (
			<div className="container">
			{
				this.props.data.map(row => (
					<div className="board-row" key={row.uniqueId}>
						<div className="yAxis">
							{row.rowKey}
						</div>
						{R.map(this.renderSquare, row.items)}
					</div>
				))
			}
				<div className="xAxis">
				</div>
			</div>
		)


	};
};
export default Matrix;
