import * as R from 'ramda';

const xAxis = (items) => {
	const xScaleKey = 'column';

	const getXLabels = R.pipe(
		R.flatten,
		R.sortBy(R.prop(xScaleKey)),
		R.map(R.prop(xScaleKey)),
		R.uniq
		)

	const xLabels = getXLabels(items);
	return xLabels;
};
const yAxis = (items) => {

	const getYLabels = R.pipe(
		R.flatten,
		R.uniq
		)

	const yLabels = getYLabels(items)
	return yLabels.sort();
};

const normalize = R.pipe(
  R.unnest,
  R.map(({items, rowKey, color, label}) => {
    return R.map(R.pipe(
    	R.assoc('rowKey', rowKey),
    	R.assoc('color', color),
    	R.assoc('label', label)
    ), items)
  }),
  R.unnest
);

const getDatasource = (dataRaw) => {
		return dataRaw.map(ds => (ds.data))
};
const setAttributes = R.pipe(
	R.map(({data, color, label}) => {
		return R.map(R.pipe(
				R.assoc('color', color),
				R.assoc('label', label)), data)
		}
	),
	R.unnest
);

const setLegendAttributes = (dataRaw, category) => {
		var legendData = [];
		dataRaw.map(row => {legendData.push(R.pick([category, 'color'], row));});
		return legendData;
};

const intersections =(circlesData)=> R.pipe(
	    	R.groupBy(({rowKey, column}) => (rowKey + column)),
	    	R.filter(R.compose(R.lt(1), R.length)),
	    	R.map(R.head),
	    	R.values
		)(circlesData);
