import { scaleOrdinal } from 'd3-scale';
import { min, max, extent } from 'd3-array';
class Scales {
  constructor(data,xLabels,yLabels, dims){
    this.setScales(xLabels,yLabels, dims);
  };
  setScales=(data,xLabels,yLabels, dims)=>{
    console.log("xLabels,yLabels",xLabels,yLabels);
    this.xScale =
      scaleOrdinal()
        .domain(xLabels)
        .range([10,dims.innerWidth-1])

    this.yScale =
      scaleOrdinal()
        .domain(yLabels)
        .range([10,dims.innerWidth-1])
  };
};
export default Scales;
