import React from 'react';
import { Square } from './utilities';
import * as R from 'ramda';

class Matrix extends React.Component {

	constructor(props) {
		super(props);
	}


	renderSquare(item, className="square") {
		return <Square value={item.value} className = {className} id={item.key}/>;
	};

	render() {
		return (
			<div className="container">
			{
				this.props.data.map(row => (
					<div className="board-row">{ R.map(this.renderSquare, row ) }</div>
				))
			}
			</div>
		)


	};
};
export default Matrix;
