import React from 'react';

class Legend extends React.Component{
	constructor(chart, options, dims, data) {
		super(chart, options);
		this.chart = chart;
		this.options = options;
		this.dims = dims;
		this.init();
	};

	init = () => {
		console.log("intra");
		this.chart.append('rect')
		.attr('x', this.dims.innerWidth )
		.attr('y', this.dims.innerHeight - 100)
		.attr('fill', '#efefef')
		.attr('class', 'legend');
	};

}

export default Legend;
