import React from 'react';
import { Square } from './utilities';


class Matrix extends React.Component {

	constructor(data) {
		super();
		console.log(data);
		this.data = data;
	};

	renderSquare(i, className="square") {
		return <Square value={i} className = {className}/>;
	};

	render() {
		return (
			<div className="container">
	 			<div className="board-row">
					{data.map(row => row.map(item => (this.renderSquare({item})) ))};
				</div> 
			</div> //container
	);};

};
export default Matrix;
