import  'd3-selection';
import 'd3-transition';

class Intersection {
	constructor(chart, data, scales){
	    this.chart = chart;
	    this.scales = scales;
	    this.updateData(data);
	};

	enter=()=>{
		this.allIntersection.enter()
			.append('rect')
			.attr('class', 'intersection')
			.attr('x', (d,i)=>i*this.scales.xScale(d["column"],d["rowKey"]))
        	.attr('y', (d)=>this.scales.yScale(d["rowKey"]))
        	.attr('fill', (d) => d["color"])
        this.allIntersection.enter()
			.append('text')
			.attr('class', 'intersection')
	      	.attr("fill","#fff")
	     	.attr("stroke-width", 1)
	      	.attr({"font-size":"18px","z-index":"999999999"})
	     	.attr("text-anchor", "middle")
	      	.text(function(d) { return ((d.y1-d.y0)*100).toFixed(0);});

	};
	updateData=(newData)=>{
	    this.data = newData;

	    this.allIntersection =
	      this.chart.selectAll('.intersection').data(this.data);
	    this.allIntersection.transition().duration(1000)
			.attr('x', (d,i)=>this.scales.xScale(d["column"],d["rowKey"]))
        	.attr('y', (d)=>this.scales.yScale(d["rowKey"]))
	        .attr('fill', (d) => d["color"])
	    this.enter();
	};
	exit=()=>{
		this.allIntersection.exit().remove();
	};


}

export default Intersection;
