import React from 'react';

class Title extends React.Component {
	constructor(chart, options) {
		super(chart, options);
		this.chart = chart;
		this.options = options;
		this.init();
	};

	init = () => {
		console.log(this.chart);
		this.chart.select('svg')
		.append('text', this.options.title);
	};
}

export default Title;
