import { scalePoint, scaleBand, scaleLinear } from 'd3-scale';

class Scales {
  constructor(xLabels,yLabels, dims){
    console.log("dimsinScales",dims);
    this.setScales(xLabels,yLabels, dims);
  };
  setScales=(xLabels,yLabels, dims)=>{
    this.xScale = scaleBand()
        .domain(xLabels)
        .range([50,dims.innerWidth-1])
        //.paddingOuter(0.5)

    this.yScale = scaleBand()
        .domain(yLabels)
        .range([50,dims.innerHeight-1])
        ///.paddingOuter(0.5)

  };
  setRadiusScale=(values)=>{
    this.rScales = scaleLinear()
        .domain([d3.min(values), d3.max(values)])
        .range([2, 10])

  };
};
export default Scales;
