import  'd3-selection';
import 'd3-transition';

class Intersection {
	constructor(chart, data, scales,dims){
	    this.chart = chart;
	    this.scales = scales;
	    this.dims = dims;
	    this.updateData(data);
	};

	enter=()=>{
		//to add ticks step or x values array len instead of the 8 in  this.dims.innerHeight/8
		this.allIntersection.enter()
			.append('path')
		    .attr("d", d3.svg.symbol().type("triangle-up").size(500))
		    .attr("transform", function(d) { return "translate(" + 100 + "," + 100 + ")"; })
		    .style("fill", "red");
			// .append('rect')
			// .attr('class', 'intersection')
			// .attr('x', (d,i)=>{return this.scales.xScale(d["column"],d["rowKey"])-this.dims.innerHeight/10})
   //      	.attr('y', (d)=>this.scales.yScale(d["rowKey"]) + 20)
   //      	.attr('fill', (d) => d["color"])

        this.allIntersection.enter()
			.append('text')
			.attr('class', 'intersection')
	      	.text('!')
	      	.attr('x', (d,i)=>{return this.scales.xScale(d["column"],d["rowKey"])-this.dims.innerHeight/12})
        	.attr('y', (d)=>this.scales.yScale(d["rowKey"]) + 35)
        console.log("dims", this.dims)

	};
	updateData=(newData)=>{
	    this.data = newData;

	    this.allIntersection =
	      this.chart.selectAll('.intersection').data(this.data);
	    this.allIntersection.transition().duration(1000)
			.attr('x', (d,i)=>this.scales.xScale(d["column"],d["rowKey"]))
        	.attr('y', (d)=>this.scales.yScale(d["rowKey"]))
	        .attr('fill', (d) => d["color"])
	    this.enter();
	};
	exit=()=>{
		this.allIntersection.exit().remove();
	};


}

export default Intersection;
