import React from 'react';
import { axisBottom, axisLeft } from 'd3-axis';


class Axes {
  constructor(parent, scales, dims, options){
    this.parent = parent;
    this.createAxes(parent, scales, dims, options);
  };
  createAxes=(parent, scales, dims, options)=>{
    this.scaleAxes(scales, dims);
    this.xAxisBottomG =
      this.parent.append('g')
        .attr('transform', `translate(0, ${dims.innerHeight})`)
        .call(this.xAxisBottom)
        .style('stroke-width', 0.1)
        .selectAll(".tick text")
        .attr("x", -dims.innerHeight/10)
        .attr('class', 'axis')
        .style('fill', options.fontColor);


    this.yAxisLeftG =
      this.parent.append('g')
        .call(this.yAxisLeft)
        .style('stroke-width', 0.1)
        .selectAll(".tick text")
        .attr("dy", -30)
        .attr('class', 'axis')
        .style('fill', options.fontColor);

  };
  scaleAxes=(scales, dims)=>{
    this.xAxisBottom =
      axisBottom()
        .scale(scales.xScale)
        .tickSize(-50)
        //.tickPadding(10)

    this.yAxisLeft =
      axisLeft()
        .scale(scales.yScale)
        .tickSize(-50)
        //.tickPadding(10)
  };
  exit=()=>{
    this.xAxisBottomG.remove();
    this.yAxisLeftG.remove();
  };
  updateAxes=(scales, dims)=>{
    this.scaleAxes(scales, dims);
    this.xAxisBottomG = this.parent.append('g')
      .attr('transform', `translate(0, ${dims.innerHeight})`)
      .call(this.xAxisBottom);

    this.yAxisLeftG = this.parent.append('g')
      .call(this.yAxisLeft);
  };
};
export default Axes;
