import React from 'react';
import { select } from 'd3-selection';
import { Dimensions, Scales, Axes, Circles, Square , XAxis, YAxis} from './utilities';
import * as R from 'ramda';

const xAxis = (items) => {
	const xScaleKey = 'column';

	const getXLabels = R.pipe(
		R.flatten,
		R.sortBy(R.prop(xScaleKey)),
		R.map(R.prop(xScaleKey)),
		R.uniq
		)

	const xLabels = getXLabels(items);
	return xLabels;
};
const yAxis = (items) => {

	const getYLabels = R.pipe(
		R.flatten,
		R.uniq
		)

	const yLabels = getYLabels(items)
	return yLabels.sort();
};
//set colors of circles
const circleColor =  [
				'#F44336',
				'#FFEBEE',
				'#FFCDD2',
				'#EF9A9A',
				'#E57373',
				'#EF5350',
				'#E53935',
				'#D32F2F',
				'#C62828',
				'#B71C1C',
				'#FF8A80',
				'#FF5252',
				'#FF1744',
				'#D50000',
				'#FCE4EC',
				'#F8BBD0',]

const normalize = R.pipe(
  R.unnest,
  R.map(({items, rowKey}) => {
    return R.map(R.assoc('rowKey', rowKey), items)
  }),
  R.unnest
);

const getDatasource = (dataRaw) => {
		return dataRaw.map(ds => (ds.data))
};
const setColors = R.pipe(
	R.indexBy(R.prop('product_id')),
	R.map((rows,index) => {
		return R.map(R.assoc('color', circleColor[index]),rows)
	}),
	R.unnest
);

class Matrix extends React.Component {
	constructor(domNodeCurrent, props) {
		super(domNodeCurrent, props);
		this.svg = select(domNodeCurrent).append('svg');
    	this.svg.attr('width', '100%').attr('height', '100%');
	};
	init=(data, dims)=>{
	    this.data = data;

	    //set chart dimensions
	    this.dims = new Dimensions(dims);

	    //create chart 
	    this.chart = this.svg.append('g');
	    this.chart.attr('transform', `translate(${this.dims.margin.left}, ${this.dims.margin.top})`);

	    //get xLabels and yLabels from data
	    const dsItems = getDatasource(this.data);
	   	var xLabels = xAxis(dsItems.map(row => (row.map(rowItems => (rowItems.items)))));
	   	var yLabels = yAxis(dsItems.map(row => (row.map(rowItems => (rowItems.rowKey)))));

	   	console.log("data", data)
	   	var dsWcolors = setColors(data);
	   	console.log("dsWcolors", dsWcolors);


	    //set scales from data, xLabels and yLabels
	    this.scales = new Scales(xLabels, yLabels, this.dims);
	    this.axes = new Axes(this.chart, this.scales, this.dims);
	    const circlesData = normalize(dsItems);

	    //draw Circles/Squares of data
	    this.circles = new Circles(this.chart, circlesData, this.scales);	  	

	};
	updateData=(data)=>{
    	this.circles.updateData(data);
  	};
  	updateDims=(dims)=>{
	    this.dims.setDims(dims);
	    this.scales.setScales(this.data, this.dims);
	    this.axes.updateAxes(this.scales, this.dims);
	    this.circles.updateScales(this.scales);
	 };
};
export default Matrix;
