import  'd3-selection';
import 'd3-transition';
import 'd3';

class Circles {
  constructor(chart, data, scales){
    this.chart = chart;
    this.scales = scales;
    this.updateData(data);
  };

  updateData=(newData)=>{
    this.data = newData;

    this.allCircles =
      this.chart.selectAll('.myCircle').data(this.data);
    this.allCircles.transition().duration(1000)
        .attr('cx', (d,i)=>this.scales.xScale(d["column"],d["rowKey"]))
        .attr('cy', (d)=>this.scales.yScale(d["rowKey"]))
        .attr('fill', (d) => d["color"])
    this.enter();
  };
  enter=()=>{
    this.allCircles.enter()
      .append('circle')
        .attr('class', 'myCircle')
        .attr('cx', (d,i)=>this.scales.xScale(d["column"],d["rowKey"]))
        .attr('cy', (d)=>this.scales.yScale(d["rowKey"]))
        .attr('r', (d) => d["value"] * 3)
        .attr('fill', (d) => d["color"])
        .attr("opacity", 0.8)
        .on("mouseover", function(d) {    
            div.transition()    
                .duration(200)    
                .attr("opacity", .9);    
            div.html(formatTime(d.date) + "<br/>"  + d.close)  
                .attr("left", (d3.event.pageX) + "px")   
                .attr("top", (d3.event.pageY - 28) + "px");  
            })          
        .on("mouseout", function(d) {   
            div.transition()    
                .duration(500)    
                .attr("opacity", 0); 
        });
    this.exit();
  };
  exit=()=>{
    this.allCircles.exit().remove();
  };
  updateScales=(newScales)=>{
    this.scales = newScales;
    this.allCircles =
      this.chart.selectAll('.myCircle')
        .prototype.transition().duration(500)
          .attr('cx', (d,i)=>this.scales.xScale(d["column"],d["rowKey"]))
          .attr('cy', (d)=>this.scales.yScale(d["rowKey"]))
  };
};
export default Circles;
