import { scalePoint, scaleBand } from 'd3-scale';

class Scales {
  constructor(xLabels,yLabels, dims){
    this.setScales(xLabels,yLabels, dims);
  };
  setScales=(xLabels,yLabels, dims)=>{
    this.xScale = scaleBand()
        .domain(xLabels)
        .range([50,dims.innerWidth-1])
        .paddingOuter(0.5)

    this.yScale = scaleBand()
        .domain(yLabels)
        .range([50,dims.innerHeight-1])
        //.paddingOuter(0.5)

  };
};
export default Scales;
