import React from 'react';
import { axisBottom, axisLeft } from 'd3-axis';


class Axes {
  constructor(parent, scales, dims, options){
    this.parent = parent;
    this.createAxes(parent, scales, dims, options);
  };
  createAxes=(parent, scales, dims, options)=>{
    this.scaleAxes(scales, dims);
    this.xAxisBottomG =
      this.parent.append('g')
        .attr('transform', `translate(0, ${dims.innerHeight})`)
        .call(this.xAxisBottom)


    this.yAxisLeftG =
      this.parent.append('g')
        .call(this.yAxisLeft)

  };
  scaleAxes=(scales, dims)=>{
    this.xAxisBottom =
      axisBottom()
        .scale(scales.xScale)
        .tickSize(-5)
        .tickPadding(20)
        .tickFormat('')


    this.yAxisLeft =
      axisLeft()
        .scale(scales.yScale)
        .tickSize(-5)
        .tickPadding(20)
        .tickFormat('')
  };
  exit=()=>{
    this.xAxisBottomG.remove();
    this.yAxisLeftG.remove();
  };
  updateAxes=(scales, dims)=>{
    this.scaleAxes(scales, dims);
    this.xAxisBottomG = this.parent.append('g')
      .attr('transform', `translate(0, ${dims.innerHeight})`)
      .call(this.xAxisBottom)

    xTicks = this.xAxisBottomG.selectAll('g.tick')

    this.yAxisLeftG = this.parent.append('g')
      .call(this.yAxisLeft);

     // this.yAxisLeftG.selectAll('.tick text')
     //    .attr('y', -25);
  };
};
export default Axes;
