import React from 'react';
import { Circles, Intersection } from './utilities';

class Legend extends React.Component{
	constructor(chart, options, dims, data, scales) {
		super(chart, options);
		this.chart = chart;
		this.options = options;
		this.dims = dims;
		this.scales = scales;

		this.legend = this.chart.append('g').attr('class', 'legend');

		this.legend
			.append('rect')
			.attr('x', this.dims.innerWidth )
			.attr('y', this.dims.innerHeight - 100)
			.attr('fill', '#efefef')
			.attr('class', 'legend'); 
		console.log("legend data", this.data);

		this.updateData(data);
	};

	updateData = (newData) => {
		this.data = newData;

		this.allLegend = this.legend.selectAll('.legendCircle').data(this.data);
		this.allLegend
	        .attr('cx', 0)
	        .attr('cy', 0)
	        .attr('fill', (d) => d.color)
   		this.enter();
	};

	enter = () => {
		this.allLegend.enter()
	        .append('circle')
	        .attr('class', 'legendCircle')
	        .attr('cx', (d,i)=> i)
	        .attr('cy', (d)=>0)
	        .attr('r', (d) => 5)
	        .attr('fill', (d) => d.color)
	        .attr("opacity", 0.8)
        this.exit();
	};
	exit=()=>{
	    this.allLegend.exit().remove();
	};

}

export default Legend;
